INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`) VALUES ('56', '3', 'Мышь OKLICK 875G ELECTRO оптическая проводная USB', 'Оригинальная игровая мышь Oklick 875G разработана специально для геймеров, однако благодаря отличным характеристикам и приемлемой цене подойдет для стандартной работы с приложениями и документами. Оптическое проводное устройство с высоким разрешением (2400 dpi) имеет высокую чувствительность и быстро откликается на команды пользователя. Мышь Oklick 875G имеет самое распространенное на сегодняшний день USB-соединение (5 but), благодаря которому она подходит для работы со стационарными компьютерами и ноутбуками. Устройство обладает нескользящей матовой поверхностью. Форма мыши разработана для праворуких пользователей. Приятным бонусом в данной модели является стильный дизайн.', '', 'Мышь OKLICK 875G ELECTRO оптическая проводная USB', '', '');
